mapper.select:
SELECT DISTINCT CONCAT_WS(',',IFNULL(FAN_DRIP_BASIC,0),IFNULL(FAP_DRIP_BASIC,0),IFNULL(FAK_DRIP_BASIC,0))
FROM T_SF_SDDGSFZDSC_M
WHERE FIELD_ID=#{fieldid} AND CRP_VRT_ID=#{vatid}

SELECT DISTINCT CONCAT_WS(',',IFNULL(FAN_DI,0),IFNULL(FAP_DI,0),IFNULL(FAK_DI,0))
FROM T_SF_SDDGSFZDSC_M
WHERE FIELD_ID=#{fieldid}
AND CRP_VRT_ID=#{vatid}  AND CRP_STG_ID=#{i} AND CRP_ETAREAL_DATE=#{date}

select count(1) from t_sf_sddgsfzdsc_m
where field_id=#{fieldid} and crp_vrt_id=#{vatid}
and crp_stg_id=#{i} and crp_etareal_date=#{date}

select id from t_sf_sddgsfzdsc_m
where field_id=#{fieldid} and crp_vrt_id=#{vatid}
and crp_stg_id=#{i} and crp_etareal_date=#{date}
